Effect.Shader:
#version 330
	
in vec3 Position;
in vec2 Tex;

out vec2 oTex;

uniform mat4 WorldMatrix;
uniform mat4 ViewMatrix;
uniform mat4 ProjectionMatrix;

void main(void)
{
	gl_Position = vec4(Position,1) * WorldMatrix * ViewMatrix * ProjectionMatrix;
	oTex = Tex;
}

#version 330

in vec2 oTex;

uniform sampler2D DiffuseMap; 

void main()
{
	vec4 diffuse = texture(DiffuseMap, oTex);
	gl_FragColor = diffuse;
}
