Effect.Shader:
#version 330

in vec3 Position;
in vec2 Tex;

out vec2 oTex;

uniform mat4 World;
uniform mat4 ViewProj;

void main()
{
	gl_Position = vec4(Position, 1.0) * World * ViewProj;
	oTex = vec2(Tex.x, 1-Tex.y);
}

#version 330

in vec2 oTex;

out vec4 FragColor;

uniform sampler2D SkyPlaneMap;

void main()
{
	vec4 diffuse = texture(SkyPlaneMap, oTex);
	FragColor = diffuse;
}

#version 330

in vec3 Position;

out vec3 oTex;

uniform mat4 World;
uniform mat4 ViewProj;

void main()
{
	vec4 posH = vec4(Position, 1.0) * World * ViewProj;
	
	oTex = Position.xyz;

	// we set z = w so that z/w == 1, always on far plane
	gl_Position = posH.xyww;
}

#version 330

in vec3 oTex;

out vec4 FragColor;

uniform samplerCube SkyCubeMap;

void main()
{
	FragColor = texture(SkyCubeMap, oTex);
}
